function addTwoHugeNumbers(a, b) {

  let result = [];
  let remainder = 0;

  const addLeadingZeroes = (numStr) => {
    while (numStr.length < 4) {
      numStr = '0' + numStr;
    }
    return parseInt(numStr);
  }

  const removeLeadingZeroes = (numStr) => {
    while (string[0] === '0' && string.length > 1) {
      string[0] = '';
    }
    return parseInt(numStr);
  }

  const populateQueue = (list, queue) => {
    queue.push(list.value);
    if (list.next) {
      return populateQueue(list.next, queue);
    } else {
      return queue;
    }
  }

  let aQueue = populateQueue(a, []);
  let bQueue = populateQueue(b, []);

  const addVals = (aVal, bVal) => {
    let sum;
    if (aVal !== undefined && bVal !== undefined) {
      sum = addLeadingZeroes(aVal.toString()) + addLeadingZeroes(bVal.toString()) + remainder;
    }
    if (aVal === undefined && bVal === undefined) {
      if (remainder) {
        result.push(1)
      }
      return;
    }
    if (aVal === undefined) {
      sum = addLeadingZeroes(bVal.toString()) + remainder;
    }
    if (bVal === undefined) {
      sum = addLeadingZeroes(aVal.toString()) + remainder;
    }
    if (sum > 9999) {
      sum -= 10000;
      remainder = 1;
    } else {
      remainder = 0;
    }
    result.push(sum);
    addVals(aQueue.pop(), bQueue.pop());
  }

  addVals(aQueue.pop(), bQueue.pop());

  return result.reverse();

}

